from random import*def createletters(letter):    for m in range(0, 26):        letter.append(alphabet)def createboard(level, option):    print("function1")    if option.lower() == "easy":        level = 7    elif option.lower() == "medium":        level = 9    elif option.lower() == "hard":        level = 12    for i in range(0, level):        board.append([])        for j in range(0, level):            board[i].append(" ")    return leveldef word(lines, choose):    while choose < level:        randomnum = randint(0, 99)        wordC = lines[randomnum]        if word not in chosen:            chosen.append(wordC.strip())            choose = choose + 1def addwords(level):    for i in range(0, level):        direction = randint(1, 2)        while True:            xlocation = randint(0, level - len(chosen[i]))            ylocation = randint(0, level - 1)            empty = True            for j in range(len(chosen[i])):                if board[ylocation][xlocation + j] != ' ':                    empty = False                    break            if empty:                if direction == 1:                    for j in range(len(chosen[i])):                        board[ylocation][xlocation + j] = chosen[i][j]                else:                    for j, k in zip(range(len(chosen[i]), 0, -1), range(len(chosen[i]))):                        board[ylocation][xlocation + k] = chosen[i][j-1]                breakdef addletters(level, letter):    for x in range(0, level):        for y in range(0, level):            v = randint(0, 25)            if board[x][y] == ' ':                board[x][y] = letter[v]'''def add():    length =    for i in range (0, length):'''pick = 1level = 0randomnum = 0choose = 0board = []chosen = []splitword = []letter = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm', 'n', 'o', 'p', 'q', 'r', 's', 't', 'u', 'v',          'w', 'x', 'y', 'z']lines = open("words.txt", "r").readlines()alphabet = open("alphabet.txt", "r").readlines()# letter.append(alphabet.strip())print(letter)'''option = input("Choose level: Easy, Medium, Hard.\n")while option.lower() != "easy" and option.lower() != "medium" and option.lower() != "hard":    option = input("Choose level: Easy, Medium, Hard.\n")'''while True:    option = input("Choose level: Easy, Medium, Hard.\n").lower().strip()    if option == 'easy' or option == 'medium' or option == 'hard':        breaklevel = createboard(level, option)word(lines, choose)addwords(level)print(chosen)addletters(level, letter)for i in range(level):    print(board[i])