from random import*def createboard(level, option):    for i in range(0, level):        board.append([])        for j in range(0, level):            board[i].append(" ")def word(waiting, choose):    while choose < waiting:        randomnum = randint(0, 99)        wordC = lines[randomnum]        if wordC not in chosen:            chosen.append(wordC.strip())            choose = choose + 1def addwords(level):    for i in range(0, waiting):        direction = randint(1, 3)        tries = 0        while True:            empty = True            tries = tries + 1            if direction == 1 or direction == 2:                xlocation = randint(0, level - len(chosen[i]))                ylocation = randint(0, level - 1)                for j in range(len(chosen[i])):                    if board[ylocation][xlocation + j] != ' ':                        empty = False                        break                if empty:                    if direction == 1:                        for j in range(len(chosen[i])):                            board[ylocation][xlocation + j] = chosen[i][j]                    else:                        for j, k in zip(range(len(chosen[i]), 0, -1), range(len(chosen[i]))):                            board[ylocation][xlocation + k] = chosen[i][j-1]                    break                if tries < 100:                    direction = 3            else:                location1 = randint(0, level - len(chosen[i]))                location2 = randint(0, level - 1)                for j in range(len(chosen[i])):                    if board[location1 + j][location2] != ' ' and board[location1 + j][location2] != chosen[i][j]:                        empty = False                        break                if empty:                    for j in range(len(chosen[i])):                        board[location1+j][location2] = chosen[i][j]                    break                if tries < 100:                    direction = 1def addletters(level, letter):    for x in range(0, level):        for y in range(0, level):            v = randint(0, 25)            if board[x][y] == ' ':                board[x][y] = letter[v]def wordguess(user):    if user in chosen and user not in foundwords:        foundwords.append(user)        return True    else:        return Falseletter = ['a', 'b', 'c', 'd', 'e', 'f', 'g', 'h', 'i', 'j', 'k', 'l', 'm', 'n', 'o', 'p', 'q', 'r', 's', 't', 'u', 'v',          'w', 'x', 'y', 'z']lines = open("words.txt", "r").readlines()while True:    level = 0    randomnum = 0    choose = 0    board = []    chosen = []    splitword = []    foundwords = []    wordsfound = 0    waiting = 0    while True:        option = input("Choose level: Easy, Medium, Hard.\n").lower().strip()        if option == 'easy' or option == 'medium' or option == 'hard':            break    if option.lower() == "easy":        level = 7        waiting = 5    elif option.lower() == "medium":        level = 9        waiting = 10    elif option.lower() == "hard":        level = 12        waiting = 15    createboard(level, option)    word(waiting, choose)    addwords(level)    print(chosen)    addletters(level, letter)    while wordsfound < level:        for row in range(0, level):            for col in range(0, level):                line = board[row][col]                print(line, end=" ")            print('')        user = input("Input a found word. Words can be horizontal (forwards and backwards).\n")        if wordguess(user):            wordsfound = wordsfound + 1            print("Nice! You found", user+".")        else:            print("Incorrect.")    print("Congrats! You win!")    while True:        play = input("Would you like to play again? (yes or no)\n").lower()        if play == 'yes' or play == 'no':            break    if play == 'no':        breakprint("Thanks for playing Becky's Word Search.")